    def click_showplot(self):
        timing.showplot()

    def click_record(self):
        rec.start()
        print("record clicked")

    def click_stoprec(self):
        rec.stop()
        print("recstop_clicked")
        rec.save("output.wav")

    def click_timealyze(self):
        bpm = int(self.bpm.text())
        offsetms = int(self.offsetms.value())
        threshold = int(self.threshold.value())
        deadzone = int(self.deadzone.value())
        subdiv = 1 if self.quarter.isChecked() else 2 if self.eighth.isChecked() else 3 if self.triplets.isChecked() else 4 if self.sixteenth.isChecked() else 1
        cappeaks = self.cappeaks.checkState()
        showplot = self.showplot.isChecked()

        sigmams, numbeats = timing.timealyze(bpm, offsetms, threshold, deadzone, subdiv, cappeaks, showplot)
        score = 100-0.3*sigmams

        print("timealyze clicked")

        self.score.setText("{} %".format(str(round(score,2))))
        self.numbeats.setText(str(numbeats))


